import React, { useRef, useEffect, useState } from 'react';
import { Container, Row, Button } from 'reactstrap';
import { NavLink, Link, useNavigate } from 'react-router-dom';
import axios from 'axios'; // For API calls
import logo from "../../assets/images/logo.png";
import './header.css';

const nav_links = [
  {
    path: '/home',
    display: 'Home',
  },
  {
    path: '/about',
    display: 'About',
  },
  {
    path: '/tours',
    display: 'Tours',
  },
];

const Header = () => {
  const headerRef = useRef(null);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const stickyHeaderFunc = () => {
    window.addEventListener('scroll', () => {
      if (headerRef.current) { // Ensure headerRef.current is not null
        if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
          headerRef.current.classList.add('sticky_header');
        } else {
          headerRef.current.classList.remove('sticky_header');
        }
      }
    });
  };
  
  useEffect(() => {
    stickyHeaderFunc();
  
    // Cleanup the event listener on component unmount
    return () => window.removeEventListener('scroll', stickyHeaderFunc);
  }, []);

  useEffect(() => {
    // Fetch user data from localStorage or API
    const fetchUser = async () => {
      const token = localStorage.getItem('token'); // Assuming you store the token after login
      if (token) {
        try {
          const response = await axios.get('/api/auth/user', {
            headers: { Authorization: `Bearer ${token}` },
          });
          setUser(response.data.user); // Update user state with data from the backend
        } catch (error) {
          console.error('Error fetching user:', error);
          localStorage.removeItem('token'); // Remove invalid token
        }
      }
    };
    fetchUser();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/login');
  };

  return (
    <header className="header w-full h-[80px]" ref={headerRef}>
      <Container>
        <Row>
          <div className="nav_wrapper d-flex align-items-center justify-content-between">
            <div className="logo">
              <img className="w-1/6" src={logo} alt="Logo" />
            </div>

            <div className="navigation">
              <ul className="menu d-flex align-items-center gap-5">
                {nav_links.map((item, index) => (
                  <li className="nav_item" key={index}>
                    <NavLink to={item.path} className={(navClass) => navClass.isActive ? "active_link" : ""}>
                      {item.display}
                    </NavLink>
                  </li>
                ))}
              </ul>
            </div>

            {/* Right Section */}
            <div className="nav_right d-flex align-items-center gap-4">
              <div className="nav_btns d-flex align-items-center gap-4">
                {user ? (
                  <>
                    <span className="welcome_text text-black">Welcome, {user.username}!</span>
                    <Button className="btn text-black bg-white" onClick={handleLogout}>
                      Logout
                    </Button>
                  </>
                ) : (
                  <>
                    <Button className="btn text-black bg-white">
                      <Link to="/login">Login</Link>
                    </Button>
                    <Button className="btn text-black bg-white">
                      <Link to="/register">Register</Link>
                    </Button>
                  </>
                )}
              </div>

              <span className="mobile_menu">
                <i className="ri-menu-line"></i>
              </span>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
