import React,{useRef,useEffect} from 'react';
import {Container,Row,Button} from 'reactstrap'
import { NavLink,Link } from 'react-router-dom';
import logo from "../../assets/images/logo.png"
import './header.css'
const nav_links= [
  {
  path:'/home',
  display: 'Home'
},
{
  path:'/about',
  display: 'About'
},
{
  path:'/tours',
  display: 'Tours'
},
]
const Header = ({user}) => {
  console.log(user.username)
  const headerRef = useRef(null)
   const stickyHeaderFunc = ()=>{  window.addEventListener('scroll', () =>{  if(document.body.scrollTop >80 || document.documentElement.scrollTop>80){
     headerRef.current.classList.add('sticky_header') }
     else{
      headerRef.current.classList.remove('sticky_header') 
     }
   })
   }
   useEffect(()=>{
    stickyHeaderFunc();
    return window.removeEventListener("scroll",stickyHeaderFunc);
   })
  return <header className='header w-full h-[80px] ' ref={headerRef}>
    <Container>
      <Row>
        <div className='nav_wrapper d-flex align-items-center justify-content-between'>
          <div className='logo'>
            <img className='w-1/6' src={logo} alt="" />
          </div>
          <div className='navigation'>
            <ul className='menu d-flex align-items-center gap-5'>
              {
                nav_links.map((item,index)=>(
                  <li className='nav_item' key={index}>
                    <NavLink to={item.path} className={navClass => navClass.isActive?"active_link" :""} >{item.display}</NavLink>
                  </li>
                ))
              }

            </ul>
          </div>

         {/* Right Section */}
         <div className="nav_right d-flex align-items-center gap-4">
              <div className="nav_btns d-flex align-items-center gap-4">
                {user ? (
                  <span className="welcome_text text-black">Welcome, {user.username}!</span>
                ) : (
                  <>
                    <Button className="btn text-black bg-white">
                      <Link to="/login">Login</Link>
                    </Button>
                    <Button className="btn text-black bg-white">
                      <Link to="/register">Register</Link>
                    </Button>
                  </>
                )}
              </div>
            
            <span className='mobile_menu'>
            <i class="ri-menu-line"></i>
            </span>
          </div>
        </div>
      </Row>
    </Container>
  </header>
};

export default Header;
